import * as rp from "request-promise";
import * as cheerio from "cheerio";

const regex = /[^A-Za-z _-]/g;
const cardNameRegex = /[^A-Za-z /]/g;
const spaces = / /g;

export const getDecksFromUrl = (wotcUrl: string): Result[] => {
    const results: Result[] = [];
    if (wotcUrl.startsWith("https://magic.wizards.com")) {
        const options = {
            uri: `https://cors-anywhere.herokuapp.com/${wotcUrl}`,
            headers: { 'Origin': 'https://feremiyjeenyus.github.io/mtgo-results-scraper/' },
            transform: function (body: any) {
                return cheerio.load(body);
            }
        };

        rp.default(options)
            .then($ => {
                $(".deck-group").each(function (i: any, elem: any) {
                    const headerContent = $(this)
                        .find("h4")
                        .text();
                    const username = headerContent.split(" (")[0];
                    const parts = headerContent.split(" (");
                    const name = parts[0];
                    let chaff = "";
                    if (parts[1]) {
                        chaff = parts[1]
                            .replace(regex, "")
                            .replace(spaces, "_")
                            .toLowerCase();
                    }
                    const url = `${wotcUrl}#${name
                        .replace(regex, "")
                        .replace(spaces, "_")
                        .toLowerCase()}${chaff ? "_" + chaff : ""}`;

                    const deck: Deck = {
                        maindeck: [],
                        sideboard: []
                    };
                    $(this).find(".sorted-by-overview-container").find(".row").each(function (i: any, elem: any) {
                        deck.maindeck.push(
                            {
                                name: $(this).find(".card-name").text().toUpperCase().trim().replace(cardNameRegex, ""),
                                count: parseInt($(this).find(".card-count").text(), 10)
                            }
                        )
                    })

                    $(this).find(".sorted-by-sideboard-container").find(".row").each(function (i: any, elem: any) {
                        deck.sideboard.push({
                            name: $(this).find(".card-name").text().toUpperCase().trim().replace(cardNameRegex, ""),
                            count: parseInt($(this).find(".card-count").text(), 10)
                        })
                    })

                    results.push({
                        pilot: username,
                        url: url,
                        deck: deck
                    });
                });

            })
            .catch(err => {
                console.log(err);
            });
    }
    return results
};
