import React, { useState } from 'react';
import "semantic-ui-css/semantic.min.css"
import { Header, Container, Grid, Input, Button, TextArea } from 'semantic-ui-react'
import './App.css';
import { getDecksFromUrl } from './scraper'
import { Result } from './types'

const App: React.FC = () => {
  const [hasScraped, setHasScraped] = useState<boolean>(false)
  const [wotcUrl, setWotcUrl] = useState<string>("");
  const [results, setResults] = useState<Result[]>([]);
  const [markup, setMarkup] = useState<string>("");

  const generateMarkup = (result: Result, usernames: string[]) => {
    const link = wotcUrl.includes('events/coverage') ? `* [${result.pilot.replace(/[_]/g, "\\_").split(' - ')[1]}](${result.url}): **${result.pilot.replace(/[_]/g, "\\_").split(' - ')[0]}${
      usernames.includes(result.pilot) ? " (duplicate pilot, link points to other list)" : ""
      }**` :
      `* [archetype](${result.url}): **${result.pilot.replace(/[_]/g, "\\_")}${
      usernames.includes(result.pilot) ? " (duplicate pilot, link points to other list)" : ""
      }**`;
    usernames.push(result.pilot);
    return link;
  }

  const scrape = async () => {
    const scrapedResults = await getDecksFromUrl(wotcUrl);
    const usernames = scrapedResults.map(r => r.pilot);
    const mu = scrapedResults.map(result => {
      generateMarkup(result, usernames)
    });
    setResults(scrapedResults);
    console.log(mu.join("\r\n"))
    setMarkup(mu.join("\r\n"));
  }

  return (
    <Container className="App">
      <Header>Scraper</Header>
      <Grid columns={2} divided >
        <Input value={wotcUrl} onChange={(e) => setWotcUrl(e.target.value)}></Input>
        <Button onClick={scrape}></Button>
        <Container>{markup}</Container>
      </Grid>
    </Container>
  );
}

export default App;
