import React, { useState, useEffect, ChangeEvent } from 'react';
import "semantic-ui-css/semantic.min.css"
import { Header, Container, Grid, Input, Button, TextArea, Form, Divider, List, Modal } from 'semantic-ui-react'
import './App.css';
import { getDecksFromUrl } from './scraper'
import { Result, Card } from './types'
import DeckList from './DeckList'

const App: React.FC = () => {
  const [hasScraped, setHasScraped] = useState<boolean>(false)
  const [wotcUrl, setWotcUrl] = useState<string>("");
  const [results, setResults] = useState<Result[]>([]);
  const [markup, setMarkup] = useState<string>("");
  const [displayedDeck, setDisplayedDeck] = useState<Result>();
  const [displayedDeckIndex, setDisplayedDeckIndex] = useState<number>();

  useEffect(() => {
    if (results) {
      setDisplayedDeck(results[0])
      setDisplayedDeckIndex(0)
    }
  }, [results]);

  const generateMarkup = (results: Result[]) => {
    const mu: string[] = [];
    for (const result of results) {
      mu.push(wotcUrl.includes('events/coverage') ? `* [${result.pilot.replace(/[_]/g, "\\_").split(' - ')[1]}](${result.url}): **${result.pilot.replace(/[_]/g, "\\_").split(' - ')[0]}${
        result.duplicatePilot ? " (duplicate pilot, link points to other list)" : ""
        }**` :
        `* [${result.archetype || 'archetype'}](${result.url}): **${result.pilot.replace(/[_]/g, "\\_")}${
        result.duplicatePilot ? " (duplicate pilot, link points to other list)" : ""
        }**`)
    }
    setMarkup(mu.join("\r\n"));
  }

  const scrape = async () => {
    const scrapedResults = await getDecksFromUrl(wotcUrl);
    generateMarkup(scrapedResults)
    setHasScraped(true);
    setResults(scrapedResults);
  }
  const toggleCardHighlight = (card: Card) => {
    card.highlighted = !card.highlighted;
  }

  const cards = displayedDeck && displayedDeck.deck.maindeck.map((card: Card) => {
    return (
      <List.Item key={card.name} onClick={() => toggleCardHighlight(card)} className={card.highlighted ? 'highlight' : ''}>
        {card.count} {card.name}
      </List.Item>
    )
  })

  const setNextDeck = () => {
    setDisplayedDeck(results[displayedDeckIndex! + 1])
    setDisplayedDeckIndex(displayedDeckIndex! + 1);
  }

  const handleSetArchtype = (e: ChangeEvent, data: any) => {
    const { value } = data
    const deck: Result = { ...displayedDeck!, archetype: value as string }
    setDisplayedDeck(deck)
  }

  return (
    <Container className="App">
      <Header>Scraper</Header>
      <Grid columns={2} divided >
        <Grid.Row>
          <Input value={wotcUrl} onChange={(e) => setWotcUrl(e.target.value)}></Input>
          <Button onClick={scrape} content="Scrape" />
          <a
            href='https://magic.wizards.com/en/content/deck-lists-magic-online-products-game-info'
            target='_blank'>
            MTGO Results
          </a>
        </Grid.Row>

        <Grid.Row>
          {displayedDeck &&
            <Container className="Deck">
              <Input label="archtype" value={displayedDeck.archetype} onChange={handleSetArchtype} />
              <Button onClick={setNextDeck} content="Next" />
              <List>
                {cards}
              </List>
            </Container>}
        </Grid.Row>

        <Grid.Row>
          <Form>
            <Form.TextArea value={markup} style={{ width: 900 }}></Form.TextArea>
          </Form>
        </Grid.Row>
      </Grid>
      {/* <Modal open={modalOpen}></Modal> */}
    </Container>
  );
}

export default App;
