import React, { useState } from 'react';
import "semantic-ui-css/semantic.min.css"
import { Header, Container, Grid, Input, Button, TextArea, Form, Divider } from 'semantic-ui-react'
import './App.css';
import { getDecksFromUrl } from './scraper'
import { Result } from './types'

const App: React.FC = () => {
  const [hasScraped, setHasScraped] = useState<boolean>(false)
  const [wotcUrl, setWotcUrl] = useState<string>("");
  const [results, setResults] = useState<Result[]>([]);
  const [markup, setMarkup] = useState<string>("");

  const generateMarkup = (results: Result[]) => {
    const mu: string[] = [];
    for (const result of results) {
      mu.push(wotcUrl.includes('events/coverage') ? `* [${result.pilot.replace(/[_]/g, "\\_").split(' - ')[1]}](${result.url}): **${result.pilot.replace(/[_]/g, "\\_").split(' - ')[0]}${
        result.duplicatePilot ? " (duplicate pilot, link points to other list)" : ""
        }**` :
        `* [archetype](${result.url}): **${result.pilot.replace(/[_]/g, "\\_")}${
        result.duplicatePilot ? " (duplicate pilot, link points to other list)" : ""
        }**`)
    }
    setMarkup(mu.join("\r\n"));
  }

  const scrape = async () => {
    const scrapedResults = await getDecksFromUrl(wotcUrl);
    generateMarkup(scrapedResults)
    setResults(scrapedResults);
  }

  return (
    <Container className="App">
      <Header>Scraper</Header>
      <Grid columns={2} divided >
        <Grid.Row>
          <Input value={wotcUrl} onChange={(e) => setWotcUrl(e.target.value)}></Input>
          <Button onClick={scrape}></Button>
        </Grid.Row>
        <Divider horizontal />
        <Grid.Row>
          <Form>
            <Form.TextArea value={markup} style={{ width: 900 }}></Form.TextArea>
          </Form>
        </Grid.Row>
      </Grid>
    </Container>
  );
}

export default App;
